import React, { useState } from "react";
import "./index.css";


const languages = [
  { name: "JavaScript", imageSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1Glz7EPJ3L6p5X7AUaYf4tw1GaYhl6R0iZA&usqp=CAU" },
  { name: "React.js", imageSrc: "data:image/png;base64," },
  { name: "Node.js", imageSrc: "data:image/png;base64," },
  { name: "Next.js", imageSrc: "data:image/png;base64," },
  { name: "Python", imageSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFIa_0ghNQrgkA_XCmZQfQZrGOJWOXFyQ9sZb_EcYWvU7I5QGODN779_56EuZGEJN1Cbo&usqp=CAU" },
  { name: "C#", imageSrc: "data:image/png;base64," },
  { name: "Tailwind", imageSrc: "data:image/jpeg;base64," },
  { name: "Bootstrap", imageSrc: "data:image/jpeg;base64," },
  

];

const SelectionSection = () => {
  const [startIndex, setStartIndex] = useState(0);

  const handlePrev = () => {
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };

  const handleNext = () => {
    if (startIndex + 3 < languages.length) {
      setStartIndex(startIndex + 1);
    }
  };

  return (
    <div className="selection-section">
      <h2>Skills</h2>
      <div className="languages-container">
        {languages.slice(startIndex, startIndex + 3).map((language, index) => (
          <div key={index} className="language-item">
            <img src={language.imageSrc} alt={language.name} />
            <p>{language.name}</p>
          </div>
        ))}
      </div>
      <div className="navigation-buttons">
        <button className="navigation-buttons-left" onClick={handlePrev} disabled={startIndex === 0}>
          &#8249;
        </button>
        <button className="navigation-buttons-right" onClick={handleNext} disabled={startIndex + 3 >= languages.length}>
          &#8250;
        </button>
      </div>
    </div>
  );
};

export default SelectionSection;
